import HeroSection from './components/HeroSection';
import Navigation from './components/Navigation';
import './styles/globals.css';

function App() {
  return (
    <div style={{
      backgroundColor: '#0A0B0F',
      color: '#FFFFFF',
      minHeight: '100vh',
      overflow: 'hidden',
      position: 'relative',
    }}>
      <Navigation />

      <main style={{
        maxWidth: '1400px',
        margin: '60px auto',
        padding: '0 40px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        position: 'relative',
      }}>
        <HeroSection />

        <div style={{
          position: 'relative',
          width: '600px',
          height: '600px',
          animation: 'fadeIn 1s ease-out',
        }}>
          {/* Main Circle */}
          <div style={{
            position: 'absolute',
            width: '400px',
            height: '400px',
            top: '50%',
            right: '50px',
            transform: 'translateY(-50%)',
            borderRadius: '50%',
            background: 'linear-gradient(45deg, rgba(97, 218, 251, 0.1), rgba(97, 218, 251, 0.05))',
            border: '2px solid rgba(97, 218, 251, 0.2)',
            animation: 'rotate 20s linear infinite',
          }}>
            {/* Orbital Rings */}
            {[0, 1, 2].map((index) => (
              <div key={`ring-${index}`} style={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                width: `${300 + index * 50}px`,
                height: `${300 + index * 50}px`,
                transform: 'translate(-50%, -50%)',
                borderRadius: '50%',
                border: '1px solid rgba(97, 218, 251, 0.1)',
                animation: `rotate ${15 - index * 3}s linear infinite ${index * 0.5}s`,
              }} />
            ))}

            {/* Tech Icons */}
            {[
              { icon: '⚛️', delay: 0 },    // React
              { icon: '🔷', delay: 0.2 },   // TypeScript
              { icon: '🟨', delay: 0.4 },   // JavaScript
              { icon: '🎨', delay: 0.6 },   // CSS
              { icon: '🔧', delay: 0.8 }    // Tools
            ].map((item, index) => (
              <div key={`icon-${index}`} style={{
                position: 'absolute',
                width: '50px',
                height: '50px',
                borderRadius: '50%',
                backgroundColor: 'rgba(97, 218, 251, 0.1)',
                backdropFilter: 'blur(5px)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '24px',
                top: `${50 + Math.sin(index * 72 * Math.PI / 180) * 150}%`,
                left: `${50 + Math.cos(index * 72 * Math.PI / 180) * 150}%`,
                transform: 'translate(-50%, -50%)',
                animation: `
                  float ${2 + index * 0.5}s infinite ease-in-out,
                  fadeIn 0.5s ease-out forwards ${item.delay}s
                `,
                boxShadow: '0 0 20px rgba(97, 218, 251, 0.2)',
              }}>
                {item.icon}
              </div>
            ))}
          </div>

          {/* Floating Code Blocks */}
          {[
            { top: '10%', right: '20%', rotate: '-15deg' },
            { top: '70%', right: '10%', rotate: '10deg' },
          ].map((pos, index) => (
            <div key={`code-${index}`} style={{
              position: 'absolute',
              width: '180px',
              padding: '15px',
              backgroundColor: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '10px',
              backdropFilter: 'blur(5px)',
              border: '1px solid rgba(97, 218, 251, 0.2)',
              ...pos,
              transform: `rotate(${pos.rotate})`,
              animation: `float ${3 + index}s infinite ease-in-out ${index * 0.5}s`,
            }}>
              <div style={{
                color: '#61DAFB',
                fontSize: '12px',
                fontFamily: 'monospace',
              }}>
                {index === 0 ? 
                  '<div className="code">' : 
                  'const dev = 🚀;'}
              </div>
            </div>
          ))}

          {/* Glowing Spheres */}
          {[1, 2, 3].map((index) => (
            <div key={`glow-${index}`} style={{
              position: 'absolute',
              width: `${20 + index * 15}px`,
              height: `${20 + index * 15}px`,
              backgroundColor: '#61DAFB',
              borderRadius: '50%',
              top: `${20 + index * 30}%`,
              right: `${10 + index * 25}%`,
              opacity: 0,
              filter: 'blur(5px)',
              animation: `
                pulse 2s infinite ease-in-out ${index * 0.3}s,
                fadeIn 0.5s ease-out forwards ${0.2 + index * 0.2}s
              `,
            }} />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
