import React from 'react';

function AIVisual() {
  return (
    <div className="visual-container" style={{
      position: 'relative',
      width: '600px',
      height: '600px',
    }}>
      {/* Data Visualization Cards */}
      <div style={{
        position: 'relative',
        width: '400px',
        height: '250px',
        margin: '100px auto',
      }}>
        {[0, 1, 2].map((index) => (
          <div
            key={index}
            style={{
              position: 'absolute',
              width: '400px',
              height: '250px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '20px',
              backdropFilter: 'blur(10px)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 0 30px rgba(97, 218, 251, 0.1)',
              transform: `translateY(${index * 20}px)`,
              zIndex: 3 - index,
              overflow: 'hidden',
            }}
          >
            {index === 0 && (
              <div style={{
                padding: '20px',
                height: '100%',
              }}>
                {/* Graph Lines */}
                <div style={{
                  position: 'relative',
                  height: '150px',
                  display: 'flex',
                  alignItems: 'flex-end',
                  gap: '15px',
                  marginTop: '20px',
                }}>
                  {[60, 80, 40, 90, 50, 70, 85].map((height, i) => (
                    <div key={i} style={{
                      width: '20px',
                      height: `${height}%`,
                      backgroundColor: '#61DAFB',
                      opacity: 0.7,
                      borderRadius: '4px',
                      animation: `growBar 1s ease-out ${i * 0.1}s`,
                    }} />
                  ))}
                </div>

                {/* Data Labels */}
                <div style={{
                  color: '#fff',
                  fontSize: '14px',
                  marginTop: '15px',
                  display: 'flex',
                  justifyContent: 'space-between',
                }}>
                  <span>Data Analysis</span>
                  <span style={{ color: '#61DAFB' }}>ML Models</span>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Neural Network Nodes */}
      {Array.from({ length: 12 }).map((_, index) => (
        <div
          key={`node-${index}`}
          style={{
            position: 'absolute',
            width: '15px',
            height: '15px',
            background: '#61DAFB',
            borderRadius: '50%',
            top: `${20 + (index % 4) * 25}%`,
            right: `${20 + Math.floor(index / 4) * 25}%`,
            opacity: 0.8,
            boxShadow: '0 0 20px rgba(97, 218, 251, 0.5)',
            animation: `pulse ${2 + index * 0.2}s infinite ease-in-out`,
          }}
        />
      ))}

      {/* Connection Lines */}
      <svg
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          opacity: 0.2,
        }}
      >
        {Array.from({ length: 15 }).map((_, index) => (
          <line
            key={`line-${index}`}
            x1={`${20 + Math.random() * 60}%`}
            y1={`${20 + Math.random() * 60}%`}
            x2={`${40 + Math.random() * 60}%`}
            y2={`${40 + Math.random() * 60}%`}
            stroke="#61DAFB"
            strokeWidth="1"
          />
        ))}
      </svg>

      {/* Floating Data Points */}
      {[
        { size: 40, top: '10%', right: '20%', label: 'Python' },
        { size: 30, top: '60%', right: '10%', label: 'ML' },
        { size: 35, top: '40%', right: '30%', label: 'AI' },
      ].map((point, index) => (
        <div
          key={`point-${index}`}
          style={{
            position: 'absolute',
            width: `${point.size}px`,
            height: `${point.size}px`,
            background: 'radial-gradient(circle at 30% 30%, #61DAFB, #2A9CC8)',
            borderRadius: '50%',
            top: point.top,
            right: point.right,
            opacity: 0.8,
            boxShadow: '0 0 30px rgba(97, 218, 251, 0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#fff',
            fontSize: '12px',
            fontWeight: 'bold',
            animation: `float ${2 + index}s infinite ease-in-out`,
          }}
        >
          {point.label}
        </div>
      ))}
    </div>
  );
}

export default AIVisual;
