import React, { useState } from 'react';

function Regression() {
  const [activeTab, setActiveTab] = useState('linear');

  const regressionTypes = {
    linear: {
      name: 'Linear Regression',
      equation: 'y = mx + b',
      assumptions: [
        'Linear relationship',
        'Independence',
        'Homoscedasticity',
        'Normality'
      ],
      metrics: {
        r2: '0.85',
        mse: '0.15',
        mae: '0.12'
      }
    },
    polynomial: {
      name: 'Polynomial Regression',
      equation: 'y = ax² + bx + c',
      assumptions: [
        'Non-linear relationship',
        'Independence',
        'Homoscedasticity'
      ],
      metrics: {
        r2: '0.92',
        mse: '0.08',
        mae: '0.07'
      }
    },
    multivariate: {
      name: 'Multivariate Regression',
      equation: 'y = β₀ + β₁x₁ + β₂x₂ + ... + βₙxₙ',
      assumptions: [
        'Linear relationships',
        'No multicollinearity',
        'Independence'
      ],
      metrics: {
        r2: '0.88',
        mse: '0.11',
        mae: '0.09'
      }
    }
  };

  return (
    <div className="page-container">
      <h1>Regression <span className="glow-text">Analysis</span></h1>
      <div className="content-wrapper">
        <div className="regression-tabs">
          {Object.keys(regressionTypes).map((type) => (
            <button
              key={type}
              className={`tab-button ${activeTab === type ? 'active' : ''}`}
              onClick={() => setActiveTab(type)}
            >
              {regressionTypes[type].name}
            </button>
          ))}
        </div>

        <div className="regression-content">
          <div className="equation-section">
            <h3>Mathematical Model</h3>
            <div className="equation-display">
              {regressionTypes[activeTab].equation}
            </div>
          </div>

          <div className="metrics-grid">
            {Object.entries(regressionTypes[activeTab].metrics).map(([key, value], index) => (
              <div 
                key={key}
                className="metric-card"
                style={{
                  animation: `fadeInUp ${0.3 + index * 0.1}s ease-out`
                }}
              >
                <span className="metric-label">{key.toUpperCase()}</span>
                <span className="metric-value">{value}</span>
              </div>
            ))}
          </div>

          <div className="assumptions-section">
            <h3>Key Assumptions</h3>
            <ul>
              {regressionTypes[activeTab].assumptions.map((assumption, index) => (
                <li 
                  key={index}
                  style={{
                    animation: `fadeInLeft ${0.3 + index * 0.1}s ease-out`
                  }}
                >
                  {assumption}
                </li>
              ))}
            </ul>
          </div>

          <div className="visualization-container">
            {/* Add interactive regression plot component here */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Regression;
