import './App.css';

function App() {
  return (
    <div style={{
      backgroundColor: '#0A0B0F',
      color: '#FFFFFF',
      minHeight: '100vh',
      backgroundImage: 'radial-gradient(circle at 50% 0%, #1A1B23 0%, #0A0B0F 50%)',
    }}>
      {/* Navigation */}
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px 40px',
        maxWidth: '1400px',
        margin: '0 auto',
      }}>
        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>
          KP
        </div>
        <div style={{
          display: 'flex',
          gap: '30px',
        }}>
          <a href="#about" style={{ color: '#FFFFFF', textDecoration: 'none' }}>About</a>
          <a href="#projects" style={{ color: '#FFFFFF', textDecoration: 'none' }}>Projects</a>
          <a href="#contact" style={{ color: '#FFFFFF', textDecoration: 'none' }}>Contact</a>
        </div>
      </nav>

      {/* Hero Section */}
      <main style={{
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '80px 40px',
        display: 'flex',
        alignItems: 'center',
        gap: '60px',
      }}>
        {/* Text Content */}
        <div style={{ flex: 1 }}>
          <h1 style={{
            fontSize: '64px',
            lineHeight: '1.1',
            marginBottom: '24px',
          }}>
            The Next
            <br />
            <span style={{ color: '#61DAFB' }}>Generation</span>
            <br />
            Developer
          </h1>

          <p style={{
            color: '#8F9BA8',
            fontSize: '18px',
            lineHeight: '1.6',
            marginBottom: '40px',
            maxWidth: '500px'
          }}>
            Full-stack developer specializing in building innovative web applications 
            and creating seamless digital experiences.
          </p>

          <button style={{
            backgroundColor: '#61DAFB',
            color: '#0A0B0F',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '30px',
            fontSize: '16px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 0 20px rgba(97, 218, 251, 0.3)',
          }}>
            View Projects
          </button>
        </div>

        {/* 3D Visual Element */}
        <div style={{
          flex: 1,
          position: 'relative',
          height: '500px',
        }}>
          {/* Floating Elements */}
          {['#61DAFB', '#FFFFFF', '#61DAFB'].map((color, index) => (
            <div key={index} style={{
              position: 'absolute',
              width: '20px',
              height: '20px',
              borderRadius: '50%',
              backgroundColor: color,
              top: `${20 + index * 30}%`,
              right: `${20 + index * 15}%`,
              boxShadow: `0 0 20px ${color}40`,
              animation: 'float 3s infinite ease-in-out',
              animationDelay: `${index * 0.5}s`,
            }} />
          ))}

          {/* Layered Cards */}
          {[0, 1, 2].map((index) => (
            <div key={index} style={{
              position: 'absolute',
              width: '300px',
              height: '200px',
              backgroundColor: '#1A1B23',
              borderRadius: '20px',
              top: '50%',
              left: '50%',
              transform: `translate(-50%, -50%) rotate(${index * 5}deg) translateY(${index * 20}px)`,
              boxShadow: '0 0 30px rgba(97, 218, 251, 0.1)',
              border: '1px solid rgba(97, 218, 251, 0.2)',
            }} />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
