function AIVisual() {
  return (
    <div style={{
      position: 'relative',
      width: '600px',
      height: '600px',
      perspective: '1000px',
    }}>
      {/* Credit Cards Stack */}
      {[0, 1, 2].map((index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            width: '400px',
            height: '250px',
            top: '50%',
            right: '100px',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
            borderRadius: '20px',
            transform: `translateY(-50%) translateZ(${-index * 20}px) translateY(${index * 20}px)`,
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            boxShadow: '0 0 30px rgba(97, 218, 251, 0.1)',
            transition: 'all 0.3s ease',
            animation: `float ${2 + index}s infinite ease-in-out`,
          }}
        >
          {index === 0 && (
            <div style={{
              padding: '30px',
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
            }}>
              {/* Card Logo */}
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
              }}>
                <div style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  background: 'linear-gradient(135deg, #61DAFB, #2A9CC8)',
                }} />
                <span style={{
                  color: '#fff',
                  fontSize: '20px',
                  fontWeight: 'bold',
                }}>
                  AI Bank
                </span>
              </div>

              {/* Card Number */}
              <div style={{
                color: '#fff',
                fontSize: '18px',
                letterSpacing: '2px',
              }}>
                **** **** **** 5678
              </div>
            </div>
          )}
        </div>
      ))}

      {/* Floating Spheres */}
      {[
        { size: 80, top: '10%', right: '20%' },
        { size: 40, top: '60%', right: '10%' },
        { size: 20, top: '40%', right: '30%' },
      ].map((sphere, index) => (
        <div
          key={`sphere-${index}`}
          style={{
            position: 'absolute',
            width: `${sphere.size}px`,
            height: `${sphere.size}px`,
            background: 'radial-gradient(circle at 30% 30%, #61DAFB, #2A9CC8)',
            borderRadius: '50%',
            top: sphere.top,
            right: sphere.right,
            opacity: 0.8,
            boxShadow: '0 0 30px rgba(97, 218, 251, 0.5)',
            animation: `float ${2 + index}s infinite ease-in-out ${index * 0.5}s`,
          }}
        />
      ))}
    </div>
  );
}

export default AIVisual;
