import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../../styles/navbar.css';

function Navbar() {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const location = useLocation();

  const navItems = {
    Personal: [
      { name: 'Internship Journey', path: '/personal/internship' },
      { name: 'Goals', path: '/personal/goals' },
      { name: 'University', path: '/personal/university' },
      { name: 'Logbook', path: '/personal/logbook' }
    ],
    Labs: [
      { name: 'AI', path: '/labs/ai' },
      { name: 'Automation', path: '/labs/automation' },
      { name: 'Data Visualization', path: '/labs/dataviz' },
      { name: 'Brainstorming', path: '/labs/brainstorming' },
      { name: 'Projects', path: '/labs/projects' }
    ],
    Research: [
      { name: 'Neural Networks', path: '/research/neural-networks' },
      { name: 'Classification', path: '/research/classification' },
      { name: 'Regression', path: '/research/regression' },
      { name: 'Clustering', path: '/research/clustering' },
      { name: 'Deep Learning', path: '/research/deep-learning' }
    ],
    Contract: [
      { name: 'Details', path: '/contract/details' },
      { name: 'GitHub', path: '/contract/github' },
      { name: 'Email', path: '/contract/email' },
      { name: 'Resume', path: '/contract/resume' },
      { name: 'Contact', path: '/contract/contact' }
    ]
  };

  return (
    <nav style={{
      position: 'absolute',
      width: '100%',
      top: 0,
      left: 0,
      padding: '32px 0',
      background: 'transparent',
      zIndex: 1000
    }}>
      <div style={{
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '0 20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        {/* Logo */}
        <Link to="/" style={{
          textDecoration: 'none',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}>
          <span style={{
            color: '#5CE1E6',
            fontSize: '24px',
            fontWeight: '600'
          }}>
            DS
          </span>
          <span style={{
            color: '#FFFFFF',
            fontSize: '24px',
            fontWeight: '500'
          }}>
            Portfolio
          </span>
        </Link>

        {/* Navigation Links */}
        <div style={{
          display: 'flex',
          gap: '48px',
          alignItems: 'center'
        }}>
          {Object.entries(navItems).map(([category, items]) => (
            <div
              key={category}
              className="nav-item"
              onMouseEnter={() => setActiveDropdown(category)}
              onMouseLeave={() => setActiveDropdown(null)}
            >
              <span style={{
                color: '#FFFFFF',
                fontSize: '16px',
                fontWeight: '500',
                cursor: 'pointer',
                padding: '8px 0',
                position: 'relative',
                transition: 'color 0.3s ease'
              }}>
                {category}
              </span>

              {activeDropdown === category && (
                <div style={{
                  position: 'absolute',
                  top: '100%',
                  left: '50%',
                  transform: 'translateX(-50%)',
                  background: 'rgba(0, 4, 15, 0.95)',
                  backdropFilter: 'blur(8px)',
                  borderRadius: '12px',
                  padding: '8px 0',
                  minWidth: '200px',
                  boxShadow: '0 4px 20px rgba(92, 225, 230, 0.15)',
                  border: '1px solid rgba(92, 225, 230, 0.1)',
                  marginTop: '8px'
                }}>
                  {items.map((item) => (
                    <Link
                      key={item.path}
                      to={item.path}
                      style={{
                        display: 'block',
                        padding: '12px 20px',
                        color: '#FFFFFF',
                        textDecoration: 'none',
                        fontSize: '14px',
                        transition: 'all 0.3s ease',
                        ':hover': {
                          backgroundColor: 'rgba(92, 225, 230, 0.1)',
                          color: '#5CE1E6'
                        }
                      }}
                    >
                      {item.name}
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
