import React, { useState } from 'react';
import './styles/animations.css';

function App() {
  const [activeDropdown, setActiveDropdown] = useState(null);

  // Navigation menu structure
  const navItems = {
    Projects: [
      'Machine Learning',
      'Data Analysis',
      'Deep Learning',
      'NLP Projects'
    ],
    Analysis: [
      'Statistical Analysis',
      'Predictive Modeling',
      'Data Visualization',
      'Research Papers'
    ],
    Models: [
      'Neural Networks',
      'Classification',
      'Regression',
      'Clustering'
    ],
    Contact: [
      'LinkedIn',
      'GitHub',
      'Email',
      'Resume'
    ]
  };

  return (
    <div style={{
      background: 'linear-gradient(130deg, #00040F 60%, #001F3F 100%)',
      color: '#FFFFFF',
      minHeight: '100vh',
      overflow: 'hidden',
      position: 'relative',
    }}>
      {/* Previous orbs code remains the same */}

      <nav className="fade-in-down" style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px 40px',
        maxWidth: '1400px',
        margin: '0 auto',
        backgroundColor: 'rgba(0, 4, 15, 0.8)',
        backdropFilter: 'blur(8px)',
        zIndex: 10,
        position: 'relative',
      }}>
        <div className="logo-animation">
          <span style={{ color: '#5CE1E6', fontSize: '28px' }}>DS</span>
          Portfolio
        </div>

        <div className="nav-links" style={{
          display: 'flex',
          gap: '40px',
        }}>
          {Object.entries(navItems).map(([category, items]) => (
            <div
              key={category}
              className="nav-item"
              onMouseEnter={() => setActiveDropdown(category)}
              onMouseLeave={() => setActiveDropdown(null)}
              style={{ position: 'relative' }}
            >
              <a 
                href={`#${category.toLowerCase()}`}
                className="nav-link"
                style={{
                  color: activeDropdown === category ? '#5CE1E6' : '#FFFFFF',
                  transition: 'color 0.3s ease'
                }}
              >
                {category}
              </a>

              {activeDropdown === category && (
                <div className="dropdown-menu" style={{
                  position: 'absolute',
                  top: '100%',
                  left: '50%',
                  transform: 'translateX(-50%)',
                  backgroundColor: 'rgba(0, 4, 15, 0.95)',
                  backdropFilter: 'blur(8px)',
                  borderRadius: '12px',
                  padding: '12px 0',
                  minWidth: '200px',
                  boxShadow: '0 4px 20px rgba(92, 225, 230, 0.15)',
                  border: '1px solid rgba(92, 225, 230, 0.1)',
                  marginTop: '10px',
                  animation: 'fadeIn 0.3s ease',
                }}>
                  {items.map((item, index) => (
                    <a
                      key={item}
                      href={`#${item.toLowerCase().replace(/\s+/g, '-')}`}
                      className="dropdown-item"
                      style={{
                        display: 'block',
                        padding: '10px 20px',
                        color: '#FFFFFF',
                        textDecoration: 'none',
                        transition: 'all 0.3s ease',
                        animation: `fadeInUp 0.3s ease forwards ${index * 0.05}s`,
                        opacity: 0,
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.backgroundColor = 'rgba(92, 225, 230, 0.1)';
                        e.target.style.color = '#5CE1E6';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.backgroundColor = 'transparent';
                        e.target.style.color = '#FFFFFF';
                      }}
                    >
                      {item}
                    </a>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </nav>

      {/* Rest of the main content remains the same */}
    </div>
  );
}

export default App;
