import React from 'react';
import AIVisual from './components/AIVisual';
import './styles/globals.css';

function App() {
  return (
    <div style={{
      backgroundColor: '#0A0B0F',
      color: '#FFFFFF',
      minHeight: '100vh',
      overflow: 'hidden',
      position: 'relative',
    }}>
      {/* Navigation */}
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px 40px',
        maxWidth: '1400px',
        margin: '0 auto',
      }}>
        <div style={{ 
          fontSize: '24px', 
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
        }}>
          <span style={{
            color: '#61DAFB',
            fontSize: '28px',
          }}>KP</span>
          AI Research
        </div>
        <div style={{
          display: 'flex',
          gap: '40px',
        }}>
          <a href="#about" style={{ color: '#FFFFFF', textDecoration: 'none' }}>About</a>
          <a href="#research" style={{ color: '#FFFFFF', textDecoration: 'none' }}>Research</a>
          <a href="#projects" style={{ color: '#FFFFFF', textDecoration: 'none' }}>Projects</a>
          <a href="#contact" style={{ color: '#FFFFFF', textDecoration: 'none' }}>Contact</a>
        </div>
      </nav>

      {/* Hero Section */}
      <main style={{
        maxWidth: '1400px',
        margin: '60px auto',
        padding: '0 40px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        position: 'relative',
      }}>
        {/* Left Content */}
        <div style={{ maxWidth: '600px' }}>
          <div style={{
            backgroundColor: 'rgba(97, 218, 251, 0.1)',
            padding: '8px 16px',
            borderRadius: '20px',
            display: 'inline-block',
            marginBottom: '24px',
          }}>
            <span style={{ color: '#61DAFB' }}>ðŸ¤– LLM Research Intern</span>
          </div>

          <h1 style={{
            fontSize: '72px',
            lineHeight: '1.1',
            marginBottom: '24px',
          }}>
            Advancing
            <br />
            <span style={{ color: '#61DAFB' }}>Language</span>
            <br />
            Intelligence
          </h1>

          <p style={{
            color: '#8F9BA8',
            fontSize: '18px',
            lineHeight: '1.6',
            marginBottom: '40px',
          }}>
            Exploring the frontiers of Large Language Models and Natural Language Processing.
            Focused on model optimization, prompt engineering, and AI alignment research.
          </p>

          <div style={{
            display: 'flex',
            gap: '20px',
          }}>
            <a href="#research" style={{
              backgroundColor: '#61DAFB',
              color: '#0A0B0F',
              padding: '12px 24px',
              borderRadius: '30px',
              textDecoration: 'none',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
            }}>
              View Research
            </a>
            <a href="#projects" style={{
              border: '1px solid #61DAFB',
              color: '#61DAFB',
              padding: '12px 24px',
              borderRadius: '30px',
              textDecoration: 'none',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
            }}>
              See Projects
            </a>
          </div>
        </div>

        {/* Right Content - AI Visual */}
        <AIVisual />
      </main>
    </div>
  );
}

export default App;
