import React, { useEffect } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import AIVisual from './components/AIVisual';
import Layout from './components/layout/Layout';
import './styles/animations.css';

function MainPage() {
  useEffect(() => {
    document.body.classList.add('loaded');
  }, []);

  return (
    <div className="main-container">
      <main style={{
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '40px 20px',
        display: 'grid',
        gridTemplateColumns: '1fr 1fr',
        gap: '40px',
        alignItems: 'center',
      }}>
        <div className="fade-in-left">
          <h1 style={{
            fontSize: '48px',
            marginBottom: '20px',
            lineHeight: '1.2'
          }}>
            Exploring the Frontiers of
            <span style={{ display: 'block', color: '#5CE1E6' }}>
              Data Science & AI
            </span>
          </h1>

          <p style={{
            fontSize: '18px',
            marginBottom: '30px',
            color: 'rgba(255, 255, 255, 0.8)',
            lineHeight: '1.6'
          }}>
            Welcome to my portfolio showcasing innovative projects in machine learning,
            data analysis, and artificial intelligence. Discover how data-driven
            solutions can transform ideas into reality.
          </p>

          <div style={{
            display: 'flex',
            gap: '20px'
          }}>
            <button className="button-primary">
              Explore Projects
            </button>
            <button className="button-secondary">
              View Research
            </button>
          </div>


        </div>

        <div className="fade-in-right">
          <AIVisual />
        </div>
      </main>

      <div className="orb top-left"></div>
      <div className="orb bottom-right"></div>
    </div>
  );
}

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<MainPage />} />
          {/* Reference to existing routes */}
          <Route path="/personal/*" />
          <Route path="/labs/*" />
          <Route path="/research/*" />
          <Route path="/contract/*" />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
