import React from 'react';

function AIVisual() {
  return (
    <div className="visual-container" style={{
      position: 'relative',
      width: '600px',
      height: '600px',
    }}>
      {/* Chat Interface */}
      <div style={{
        position: 'relative',
        width: '400px',
        height: '300px',
        margin: '100px auto',
        backgroundColor: 'rgba(255, 255, 255, 0.05)',
        borderRadius: '20px',
        overflow: 'hidden',
        border: '1px solid rgba(97, 218, 251, 0.2)',
        boxShadow: '0 0 30px rgba(97, 218, 251, 0.1)',
      }}>
        {/* Chat Messages */}
        <div style={{ padding: '20px' }}>
          {[
            { text: "Analyzing language patterns...", delay: 0 },
            { text: "Processing semantic context...", delay: 0.5 },
            { text: "Generating response...", delay: 1 }
          ].map((message, index) => (
            <div
              key={index}
              style={{
                backgroundColor: 'rgba(97, 218, 251, 0.1)',
                padding: '12px',
                borderRadius: '10px',
                marginBottom: '10px',
                maxWidth: '80%',
                opacity: 0,
                animation: `fadeInUp 0.5s ease-out forwards ${message.delay}s`,
              }}
            >
              <div style={{ color: '#61DAFB', marginBottom: '4px' }}>AI Model</div>
              <div style={{ color: '#FFFFFF' }}>{message.text}</div>
            </div>
          ))}
        </div>

        {/* Typing Indicator */}
        <div style={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          padding: '20px',
          borderTop: '1px solid rgba(97, 218, 251, 0.1)',
          backgroundColor: 'rgba(255, 255, 255, 0.02)',
        }}>
          <div style={{
            display: 'flex',
            gap: '4px',
          }}>
            {[0, 1, 2].map((dot) => (
              <div
                key={dot}
                style={{
                  width: '8px',
                  height: '8px',
                  backgroundColor: '#61DAFB',
                  borderRadius: '50%',
                  animation: `pulse 1s infinite ${dot * 0.2}s`,
                }}
              />
            ))}
          </div>
        </div>
      </div>

      {/* Neural Network Nodes */}
      {Array.from({ length: 15 }).map((_, index) => (
        <div
          key={`node-${index}`}
          style={{
            position: 'absolute',
            width: '10px',
            height: '10px',
            background: '#61DAFB',
            borderRadius: '50%',
            top: `${20 + Math.random() * 60}%`,
            right: `${20 + Math.random() * 60}%`,
            opacity: 0.8,
            boxShadow: '0 0 20px rgba(97, 218, 251, 0.5)',
            animation: `pulse ${2 + Math.random() * 2}s infinite ease-in-out`,
          }}
        />
      ))}

      {/* Floating Keywords */}
      {[
        { text: "GPT", size: 40, top: '10%', right: '20%' },
        { text: "BERT", size: 35, top: '60%', right: '10%' },
        { text: "NLP", size: 30, top: '40%', right: '30%' },
        { text: "LLM", size: 45, top: '25%', right: '40%' },
      ].map((keyword, index) => (
        <div
          key={`keyword-${index}`}
          style={{
            position: 'absolute',
            padding: '10px 20px',
            background: 'rgba(97, 218, 251, 0.1)',
            borderRadius: '15px',
            top: keyword.top,
            right: keyword.right,
            fontSize: `${keyword.size / 3}px`,
            color: '#61DAFB',
            backdropFilter: 'blur(5px)',
            border: '1px solid rgba(97, 218, 251, 0.2)',
            animation: `float ${2 + index}s infinite ease-in-out`,
          }}
        >
          {keyword.text}
        </div>
      ))}
    </div>
  );
}

export default AIVisual;
