import React, { useState } from 'react';
import './styles/animations.css';

function App() {
  const [activeDropdown, setActiveDropdown] = useState(null);

  // Navigation menu structure
  const navItems = {
    Personal: [
      'Internship Journey',
      'Goals',
      'University',
      'Logbook'
    ],
    Labs: [
      'AI',
      'Automation',
      'Data Visualization',
      'Brainstorming',
      'Projects'
    ],
    Research: [
      'Neural Networks',
      'Classification',
      'Regression',
      'Clustering',
      'Deep Learning'
    ],
    Contract: [
      'Details',
      'GitHub',
      'Email',
      'Resume',
      'Contact'
    ]
  };

  return (
    <div style={{
      background: 'linear-gradient(130deg, #00040F 60%, #001F3F 100%)',
      color: '#FFFFFF',
      minHeight: '100vh',
    }}>
      <nav style={{
        padding: '20px 40px',
        background: 'rgba(0, 4, 15, 0.95)',
        backdropFilter: 'blur(8px)',
        borderBottom: '1px solid rgba(92, 225, 230, 0.1)'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1400px',
          margin: '0 auto'
        }}>
          <div style={{
            fontSize: '24px',
            fontWeight: 'bold'
          }}>
            <span style={{ color: '#5CE1E6' }}>DS</span> Portfolio
          </div>

          <div style={{ display: 'flex', gap: '30px' }}>
            {Object.entries(navItems).map(([category, items]) => (
              <div
                key={category}
                style={{ position: 'relative' }}
                onMouseEnter={() => setActiveDropdown(category)}
                onMouseLeave={() => setActiveDropdown(null)}
              >
                <div style={{
                  cursor: 'pointer',
                  padding: '10px',
                  transition: 'color 0.3s ease',
                  color: activeDropdown === category ? '#5CE1E6' : '#FFFFFF'
                }}>
                  {category}
                </div>

                {activeDropdown === category && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    background: 'rgba(0, 4, 15, 0.95)',
                    backdropFilter: 'blur(8px)',
                    borderRadius: '12px',
                    padding: '10px',
                    minWidth: '200px',
                    boxShadow: '0 4px 20px rgba(92, 225, 230, 0.15)',
                    border: '1px solid rgba(92, 225, 230, 0.1)',
                    zIndex: 1000
                  }}>
                    {items.map((item, index) => (
                      <div
                        key={item}
                        style={{
                          padding: '10px 20px',
                          color: '#FFFFFF',
                          cursor: 'pointer',
                          transition: 'all 0.3s ease',
                          animation: `fadeInUp 0.3s ease forwards ${index * 0.05}s`,
                          ':hover': {
                            background: 'rgba(92, 225, 230, 0.1)',
                            color: '#5CE1E6',
                            transform: 'translateX(5px)'
                          }
                        }}
                      >
                        {item}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </nav>

      {/* Rest of the main content remains the same */}
    </div>
  );
}

export default App;
