import React, { useState } from 'react';

function Classification() {
  const [activeModel, setActiveModel] = useState('logistic');

  const models = {
    logistic: {
      name: 'Logistic Regression',
      description: 'Binary classification using logistic function',
      accuracy: '85%',
      features: [
        'Linear decision boundary',
        'Probability output',
        'Feature importance analysis'
      ],
      applications: [
        'Spam Detection',
        'Medical Diagnosis',
        'Credit Risk Assessment'
      ]
    },
    svm: {
      name: 'Support Vector Machine',
      description: 'Classification using hyperplane separation',
      accuracy: '89%',
      features: [
        'Non-linear classification',
        'Kernel tricks',
        'Margin optimization'
      ],
      applications: [
        'Image Classification',
        'Text Categorization',
        'Pattern Recognition'
      ]
    },
    randomForest: {
      name: 'Random Forest',
      description: 'Ensemble learning method using decision trees',
      accuracy: '92%',
      features: [
        'Feature importance',
        'Handles non-linearity',
        'Reduces overfitting'
      ],
      applications: [
        'Customer Segmentation',
        'Financial Analysis',
        'Disease Classification'
      ]
    }
  };

  return (
    <div className="page-container">
      <h1>Classification <span className="glow-text">Research</span></h1>
      <div className="content-wrapper">
        <div className="model-selector">
          {Object.keys(models).map((modelKey) => (
            <button
              key={modelKey}
              className={`model-button ${activeModel === modelKey ? 'active' : ''}`}
              onClick={() => setActiveModel(modelKey)}
            >
              {models[modelKey].name}
            </button>
          ))}
        </div>

        <div className="model-details">
          <div className="model-header">
            <h2>{models[activeModel].name}</h2>
            <span className="accuracy-badge">
              Accuracy: {models[activeModel].accuracy}
            </span>
          </div>

          <p className="model-description">
            {models[activeModel].description}
          </p>

          <div className="features-grid">
            <div className="features-section">
              <h3>Key Features</h3>
              <ul>
                {models[activeModel].features.map((feature, index) => (
                  <li 
                    key={index}
                    style={{
                      animation: `fadeInLeft ${0.3 + index * 0.1}s ease-out`
                    }}
                  >
                    {feature}
                  </li>
                ))}
              </ul>
            </div>

            <div className="applications-section">
              <h3>Applications</h3>
              <ul>
                {models[activeModel].applications.map((app, index) => (
                  <li 
                    key={index}
                    style={{
                      animation: `fadeInRight ${0.3 + index * 0.1}s ease-out`
                    }}
                  >
                    {app}
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <div className="visualization-section">
            {/* Add interactive visualization component here */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Classification;
